package com.example.xseries.Retrofit;import static com.example.xseries.Constants.MoviesConstants.BASE_API;import com.example.xseries.Model.MoviesModel;import com.example.xseries.Response.ImageResponse;import com.example.xseries.Response.MoviesResponse;import com.example.xseries.Response.ReviewsResponse;import com.example.xseries.Response.TrailerResponse;import retrofit2.Call;import retrofit2.http.GET;import retrofit2.http.Path;import retrofit2.http.Query;public interface ApiInterface {    @GET("movie/top_rated?api_key=" + BASE_API)    Call<MoviesResponse> getTopRatedMovies();    @GET("movie/popular?api_key=" + BASE_API)    Call<MoviesResponse> getPopMovies();    @GET("trending/movie/day?api_key=" + BASE_API)    Call<MoviesResponse> getTrendingMovies();    @GET("movie/upcoming?api_key=" + BASE_API)    Call<MoviesResponse> getUpcomingMovies();    @GET("movie/{id}/recommendations?api_key=" + BASE_API)    Call<MoviesResponse> getRecommendedMovies(@Path("id") int id);    @GET("tv/{id}/recommendations?api_key=" + BASE_API)    Call<MoviesResponse> getRecommendedTV(@Path("id") int id);    @GET("trending/tv/day?api_key=" + BASE_API)    Call<MoviesResponse> getTrendingTvShows();    @GET("search/multi?api_key=" + BASE_API)    Call<MoviesResponse> searchMoviesTvShows(@Query("query") String query);    @GET("movie/{id}?api_key=" + BASE_API + "&append_to_response=credits")    Call<MoviesModel> getMovieDetails(@Path("id") int id);    @GET("movie/{id}?api_key=" + BASE_API + "&append_to_response=credits")    Call<MoviesModel> getCastDetails(@Path("id") int id);    @GET("tv/{id}?api_key=" + BASE_API + "&append_to_response=credits")    Call<MoviesModel> getTVCastDetails(@Path("id") int id);    @GET("tv/{id}?api_key=" + BASE_API)    Call<MoviesModel> getTVDetails(@Path("id") int id);    @GET("movie/{id}/reviews?api_key=" + BASE_API)    Call<ReviewsResponse> getMovieReviews(@Path("id") int id);    @GET("tv/{id}/reviews?api_key=" + BASE_API)    Call<ReviewsResponse> getTVReviews(@Path("id") int id);    @GET("movie/{id}/videos?api_key=" + BASE_API)    Call<TrailerResponse> getMoviesTrailer(@Path("id") int id);    @GET("tv/{id}/videos?api_key=" + BASE_API)    Call<TrailerResponse> getTVTrailer(@Path("id") int id);    @GET("movie/{id}/images?api_key=" + BASE_API)    Call<ImageResponse> getMoviesImages(@Path("id") int id);    @GET("tv/{id}/images?api_key=" + BASE_API)    Call<ImageResponse> getTVImages(@Path("id") int id);}